CREATE TABLE ROLE (IDROLE INTEGER PRIMARY KEY GENERATED ALWAYS AS IDENTITY, DERNIERE_MISE_A_JOUR TIMESTAMP, DESCRIPTION VARCHAR(255), MODIFIER_PAR VARCHAR(255), ROLE VARCHAR(255));
CREATE TABLE UTILISATEUR (IDUTILISATEUR INTEGER PRIMARY KEY GENERATED ALWAYS AS IDENTITY, DERNIERE_MISE_A_JOUR TIMESTAMP, EMAIL VARCHAR(255), MODIFIER_PAR VARCHAR(255), NOM VARCHAR(255), PASSWORD VARCHAR(255), PRENOM VARCHAR(255), ROLE_FK INTEGER REFERENCES ROLE(IDROLE));
CREATE TABLE ACTION (IDACTION INTEGER PRIMARY KEY GENERATED ALWAYS AS IDENTITY, ACTION VARCHAR(255), DERNIERE_MISE_A_JOUR TIMESTAMP, DESCRIPTION VARCHAR(255), MODIFIE_PAR VARCHAR(255), ROLE_FK INTEGER REFERENCES ROLE(IDROLE));
